queries.info:

SELECT f._ref_arkivdel series_system_id, f._ref_klasse class_system_id, COUNT(*) number_of_files
FROM arkivstruktur.mappe f
GROUP BY f._ref_arkivdel, f._ref_klasse;

SELECT r._ref_arkivdel series_system_id, count(*) number_of_document_description FROM arkivstruktur.dokumentbeskrivelse d JOIN arkivstruktur.registrering r ON r.systemid = d._ref_registrering GROUP BY r._ref_arkivdel
UNION ALL
SELECT 'total', count(*) FROM arkivstruktur.dokumentbeskrivelse;

SELECT r._ref_arkivdel series_system_id, COUNT(*) document_objects, (d._ref_dokumentbeskrivelse IS NOT NULL) in_document_description, (d._ref_dokumentbeskrivelse IS NULL) in_record FROM arkivstruktur.dokumentobjekt d LEFT JOIN arkivstruktur.dokumentbeskrivelse dd ON dd._id = d._parent_id LEFT JOIN arkivstruktur.registrering r ON r._id = d._parent_id GROUP BY series_system_id, in_document_description, in_record
UNION ALL
SELECT 'total', count(*), TRUE in_document_description, TRUE in_record FROM arkivstruktur.dokumentobjekt;

queries.warnings:

SELECT systemid system_id, arkivdelstatus status, avsluttetdato finalized_date, avsluttetav finalized_by
FROM arkivstruktur.arkivdel
WHERE arkivdelstatus <> 'Avsluttet periode' OR avsluttetdato IS NULL OR avsluttetav IS NULL;

queries.info:
SELECT f._ref_arkivdel series_system_id, COALESCE(f._dtype, 'mappe') file_type, count(*) number_of_files FROM arkivstruktur.mappe f GROUP BY f._ref_arkivdel, f._dtype
UNION ALL
SELECT 'total', 'total', count(*) FROM arkivstruktur.mappe;

SELECT r._ref_arkivdel series_system_id, COALESCE(r._dtype, 'registrering') record_type, count(*) number_of_records FROM arkivstruktur.registrering r GROUP BY r._ref_arkivdel, r._dtype
UNION ALL
SELECT 'total', 'total', COUNT(*) FROM arkivstruktur.registrering;


SELECT COUNT(*) documents_with_invalid_checksums
FROM arkivstruktur.dokumentobjekt
WHERE LOWER(sjekksum) <> LOWER(_detected_checksum);

queries.errors:

SELECT referansedokumentfil document, sjekksum archive_checksum, _detected_checksum detected_checksum, 'SHA-256' algorithm
FROM arkivstruktur.dokumentobjekt
WHERE LOWER(sjekksum) <> LOWER(_detected_checksum);

queries.info:

SELECT COUNT(*) documents_with_invalid_file_type
FROM arkivstruktur.dokumentobjekt
WHERE _is_valid_type <> 'true';

queries.errors:

SELECT referansedokumentfil document, formatDetaljer archive_file_type, _detected_type detected_file_type, _is_valid_type is_file_type_valid
FROM arkivstruktur.dokumentobjekt
WHERE _is_valid_type <> 'true';
